import React, { useState } from 'react';
import './styles.css'; // Assuming styles.css file exists for styling

const EmailSignature = () => {
  const [name, setName] = useState('');
  const [jobTitle, setJobTitle] = useState('');
  const [company, setCompany] = useState('');
  const [phone, setPhone] = useState('');
  const [email, setEmail] = useState('');
  const [website, setWebsite] = useState('');
  const [address, setAddress] = useState('');

  const handleInputChange = (event) => {
    const { name, value } = event.target;
    switch (name) {
      case 'name':
        setName(value);
        break;
      case 'jobTitle':
        setJobTitle(value);
        break;
      case 'company':
        setCompany(value);
        break;
      case 'phone':
        setPhone(value);
        break;
      case 'email':
        setEmail(value);
        break;
      case 'website':
        setWebsite(value);
        break;
      case 'address':
        setAddress(value);
        break;
      default:
        break;
    }
  };

  const generateSignature = () => {
    let signature = `
      <table class="email-signature">
        <tr>
          <th class="name"><span class="math-inline">\{name\}</th\>
</tr\>
<tr\>
<td class\="job\-title"\></span>{jobTitle}</td>
        </tr>
        <tr>
          <td class="contact-info">`;
    if (phone) {
      signature += `Phone: ${phone}<br>`;
    }
    signature += `Email: <a href="mailto:<span class="math-inline">\{email\}"\></span>{email}</a>`;
    signature += `</td>
        </tr>
        <tr>
          <td class="company-info">`;
    if (company) {
      signature += `Company: ${company}<br>`;
    }
    if (website) {
      signature += `Website: <a href="<span class="math-inline">\{website\}"\></span>{website}</a><br>`;
    }
    signature += `</td>
        </tr>
      </table>`;
    return signature;
  };

  return (
    <div className="container">
      <h2>Email Signature Generator</h2>
      <form className="form">
        <label htmlFor="name">Name:</label>
        <input
          type="text"
          id="name"
          name="name"
          value={name}
          onChange={handleInputChange}
          placeholder="Enter your name"
        />
        <br />
        <label htmlFor="jobTitle">Job Title:</label>
        <input
          type="text"
          id="jobTitle"
          name="jobTitle"
          value={jobTitle}
          onChange={handleInputChange}
          placeholder="Enter your job title"
        />
        <br />
        <label htmlFor="company">Company:</label>
        <input
          type="text"
          id="company"
          name="company"
          value={company}
          onChange={handleInputChange}
          placeholder="Enter your company name (optional)"
        />
        <br />
        <label htmlFor="phone">Phone :</label>
        <input
          type="text"
          id="phone"
          name="phone"
          value={phone}
          onChange={handleInputChange}
          placeholder="Enter your phone number (optional)"
        />
        <br />
        <label htmlFor="email">Email :</label>
        <input
          type="text"
          id="email"
          name="email"
          value={email}
          onChange={handleInputChange}
          placeholder="Enter your email address"
        />
        <br />
        <label htmlFor="website">Website : </label>
        <input
          type="text"
          id="website"
          name="website"
          value={website}
          onChange={handleInputChange}
          placeholder="Enter your website URL (optional)"
        />
        <br />
        <label htmlFor="address">Address : </label>
        <input
          type="text"
          id="address"
          name="address"
          value={address}
          onChange={handleInputChange}
          placeholder="Enter your address (optional)"
        />
        <br />
        <button type="button" onClick={generateSignature}>
          Generate Signature
        </button>
            </form>
            <div dangerouslySetInnerHTML={{ __html: generateSignature() }}></div>
        </div>
    );
};

export default EmailSignature;
