import React, { useState } from 'react';
import { CopyToClipboard } from 'react-copy-to-clipboard';

const EmailSignature = () => {
    const [name, setName] = useState('');
    const [jobTitle, setJobTitle] = useState('');
    const [company, setCompany] = useState('');
    const [phone, setPhone] = useState('');
    const [email, setEmail] = useState('');
    const [website, setWebsite] = useState('');
    const [address, setAddress] = useState('');
    const [copied, setCopied] = useState(false);


    const handleInputChange = (event) => {
        const { name, value } = event.target;
        switch (name) {
            case 'name':
                setName(value);
                break;
            case 'jobTitle':
                setJobTitle(value);
                break;
            case 'company':
                setCompany(value);
                break;
            case 'phone':
                setPhone(value);
                break;
            case 'email':
                setEmail(value);
                break;
            case 'website':
                setWebsite(value);
                break;
            case 'address':
                setAddress(value);
                break;
            default:
                break;
        }
    };
    const handleCopy = () => {
        setCopied(true);
        setTimeout(() => setCopied(false), 2000);
        console.log('Signature copied to clipboard!');
    };

    const generateSignature = (e) => {
        let signatureText = `${name}\n`;
        signatureText += `${jobTitle}\n`;
        if (company) {
            signatureText += `Company: ${company}\n`;
        }
        if (phone) {
            signatureText += `Phone: ${phone}\n`;
        }
        signatureText += `<b>Ema</b>il: ${email}`;
        signatureText += `Address: ${address}`;

        let testData = document.getElementById('test').innerHTML;

        return signatureText;
    };


    return (
        <div id="test" className="container">
            <h2>Email Signature Generator</h2>
            <form className="form">
                <label htmlFor="name">Name:</label>
                <input
                    type="text"
                    id="name"
                    name="name"
                    value={name}
                    onChange={handleInputChange}
                    placeholder="Enter your name"
                />
                <br />
                <label htmlFor="jobTitle">Job Title:</label>
                <input
                    type="text"
                    id="jobTitle"
                    name="jobTitle"
                    value={jobTitle}
                    onChange={handleInputChange}
                    placeholder="Enter your job title"
                />
                <br />
                <label htmlFor="company">Company:</label>
                <input
                    type="text"
                    id="company"
                    name="company"
                    value={company}
                    onChange={handleInputChange}
                    placeholder="Enter your company name (optional)"
                />
                <br />
                <label htmlFor="phone">Phone :</label>
                <input
                    type="text"
                    id="phone"
                    name="phone"
                    value={phone}
                    onChange={handleInputChange}
                    placeholder="Enter your phone number (optional)"
                />
                <br />
                <label htmlFor="email">Email :</label>
                <input
                    type="text"
                    id="email"
                    name="email"
                    value={email}
                    onChange={handleInputChange}
                    placeholder="Enter your email address"
                />
                <br />
                <label htmlFor="website">Website : </label>
                <input
                    type="text"
                    id="website"
                    name="website"
                    value={website}
                    onChange={handleInputChange}
                    placeholder="Enter your website URL (optional)"
                />
                <br />
                <label htmlFor="address">Address : </label>
                <input
                    type="text"
                    id="address"
                    name="address"
                    value={address}
                    onChange={handleInputChange}
                    placeholder="Enter your address (optional)"
                />
                <br />
            </form>
            <div dangerouslySetInnerHTML={{ __html: generateSignature() }}></div>
            <CopyToClipboard text={generateSignature()} onCopy={handleCopy}>
                <button type="button">{copied ? 'Copied!' : 'Copy to Clipboard'}</button>
            </CopyToClipboard>
            {copied && <p>Signature copied to clipboard!</p>}
        </div>
    );
}

export default EmailSignature;
