                <input
                    type="text"
                    id="website"
                    name="website"
                    value={website}
                    onChange={handleInputChange}
                    placeholder="Enter your website URL (optional)"
                />
                <br />
                <label htmlFor="address">Address : </label>
                <input
                    type="text"
                    id="address"
                    name="address"
                    value={address}
                    onChange={handleInputChange}
                    placeholder="Enter your address (optional)"
                />
