import React, { useState } from 'react';

const EmailSignature = () => {
  const [name, setName] = useState('');
  const [jobTitle, setJobTitle] = useState('');
  const [company, setCompany] = useState('');
  const [phone, setPhone] = useState('');
  const [email, setEmail] = useState('');
  const [website, setWebsite] = useState('');

  const handleInputChange = (event) => {
    const { name, value } = event.target;
    switch (name) {
      case 'name':
        setName(value);
        break;
      case 'jobTitle':
        setJobTitle(value);
        break;
      case 'company':
        setCompany(value);
        break;
      case 'phone':
        setPhone(value);
        break;
        case 'email':
        setEmail(value);
        break;
      case 'website':
        setWebsite(value);
        break;
      default:
        break;
    }
  };

  const generateSignature = () => {
    let signature = `
      <table style="width: 100%; border-collapse: collapse; background-color: #c62035;">
        <tr>
          <th >
          <td>${name}</td></th>
          <div style={{ borderTop: '1px solid #ccc', margin: '10px 0'color: white; }} />
        </tr>
        
        <tr>
          <td style="text-align: left; padding: 5px 0;">
            <a href="mailto:${email}">${email}</a>
          </td>
        </tr>
        <tr>
          <td style="text-align: left; padding: 5px 0; border-bottom: 1px solid #ccc;">${jobTitle}</td>
        </tr>
        <tr>
          <td style="padding: 5px 0;">`;
    if (phone) {
      signature += `Phone: ${phone}<br>`;
    }
    signature += `</td>
          <td style="padding: 5px 0; border-left: 1px solid #ccc;">`;
    if (company) {
      signature += `Company: ${company}<br>`;
    }
    if (website) { 
      signature += `Website: <a href="${website}">${website}</a><br>`;
    }
    signature += `</td>
        </tr>
      </table>`;
    return signature;
  };

  return (
    <div >
      <h2>Email Signature Generator</h2>
      <form className='centered-container'>
        <label htmlFor="name">Name:</label>
        <input type="text" id="name" name="name" value={name} onChange={handleInputChange} />
        <br />
        <label htmlFor="jobTitle">Job Title:</label>
        <input type="text" id="jobTitle" name="jobTitle" value={jobTitle} onChange={handleInputChange} />
        <br />
        <label htmlFor="company">Company:</label>
        <input type="text" id="company" name="company" value={company} onChange={handleInputChange} />
        <br />
        <label htmlFor="phone">Phone :</label>
        <input type="text" id="phone" name="phone" value={phone} onChange={handleInputChange} />
        <br />
        <label htmlFor="phone">Email :</label>
        <input type="text" id="email" name="email" value={email} onChange={handleInputChange} />
        <br />
        <label htmlFor="website">Website : </label>
        <input type="text" id="website" name="website" value={website} onChange={handleInputChange} />
        <br />
        <button type="button" onClick={generateSignature}>
          Generate Signature
        </button>
      </form>
      <div dangerouslySetInnerHTML={{ __html: generateSignature() }}></div>
    </div>
  );
};

export default EmailSignature;
