import './App.css';
import React, { useState } from 'react';

function App () {
  const EmailSignature = () => {
    const [name, setName] = useState('');
    const [jobTitle, setJobTitle] = useState('');
    const [company, setCompany] = useState('');
    const [phone, setPhone] = useState('');
    const [website, setWebsite] = useState('');
    const handleInputChange = (event) => {
      const { name, value } = event.target;
      switch (name) {
        case 'name':
          setName(value);
          break;
        case 'jobTitle':
          setJobTitle(value);
          break;
        case 'company':
          setCompany(value);
          break;
        case 'phone':
          setPhone(value);
          break;
        case 'website':
          setWebsite(value);
          break;
        default:
          break;
      }
    };
    const generateSignature = () => {
      let signature = `
        <br>
        --<br>
        ${name}<br>
        ${jobTitle}<br>
        ${company}<br>
        `;
      if (phone) {
        signature += `Phone: ${phone}<br>`;
      }
      if (website) {
        signature += `Website: <a href="${website}">${website}</a><br>`;
      }
      return signature;
    };
    return (
      <div>
        <h2>Email Signature Generator</h2>
        <form>
          <label htmlFor="name">Name:</label>
          <input type="text" id="name" name="name" value={name} onChange={handleInputChange} />
          <br />
          <label htmlFor="jobTitle">Job Title:</label>
          <input type="text" id="jobTitle" name="jobTitle" value={jobTitle} onChange={handleInputChange} />
          <br />
          <label htmlFor="company">Company:</label>
          <input type="text" id="company" name="company" value={company} onChange={handleInputChange} />
          <br />
          <label htmlFor="phone">Phone (Optional):</label>
          <input type="text" id="phone" name="phone" value={phone} onChange={handleInputChange} />
          <br />
          <label htmlFor="website">Website (Optional):</label>
          <input type="text" id="website" name="website" value={website} onChange={handleInputChange} />
          <br />
          <button type="button" onClick={generateSignature}>
            Generate Signature
          </button>
        </form>
        <div dangerouslySetInnerHTML={{ __html: generateSignature() }}></div>
      </div>
    );
  };
}

export default App;
