import React, { useState } from 'react';
import { CopyToClipboard } from 'react-copy-to-clipboard';
import { toast } from 'react-toastify'; // 1. Import toast from 'react-toastify'
import 'react-toastify/dist/ReactToastify.css';
import MailTemplate from './mail_template';

const EmailSignature = () => {
    const [name, setName] = useState('');
    const [jobTitle, setJobTitle] = useState('');
    const [phone, setPhone] = useState('');
    const [whatsapp, setWhatsapp] = useState('');
    const [email, setEmail] = useState('');
    const [address, setAddress] = useState('Safiya Travels Pvt Ltd<br />39/4071B, Safiya Tower<br />Near Ravipuram Temple, Ravipuram Rd.<br />Kochi, Ernakulam, Kerala, India, 682016');
    const [copied, setCopied] = useState(false);
    const [image, setImage] = useState(null);
    const [imageUrl, setImageUrl] = useState(null);

    const handleInputChange = (event) => {
        const { name, value } = event.target;

        switch (name) {
            case 'name':
                setName(value);
                break;
            case 'jobTitle':
                setJobTitle(value);
                break;
            case 'phone':
                setPhone(value);
                break;
            case 'whatsapp':
                setWhatsapp(value);
                break;
            case 'email':
                setEmail(value);
                break;
            case 'address':
                setAddress(value);
                break;
            case 'photo':
                const selectedFile = event.target.files[0];
                setImage(selectedFile);
                break;
            default:
                break;
        }
    };

    function copyToClip(htmlContent, imageDataURL) {
        let signatureText = `...`; // Your signature text
        function listener(e) {
            e.clipboardData.setData("text/html", signatureText);
            e.clipboardData.setData("text/plain", signatureText);
            e.preventDefault();
        }

        document.addEventListener("copy", listener);
        document.execCommand("copy");
        document.removeEventListener("copy", listener);
    };

    const generateSignature = () => {
        let signatureText = `...`; // Your signature text
        return signatureText;
    };

    const handlePhotoChange = (event) => {
        const selectedFile = event.target.files[0];
        setImage(selectedFile);
        const reader = new FileReader();
        reader.onload = (event) => {
            setImageUrl(event.target.result);
        };
        reader.readAsDataURL(selectedFile);
    };

    return (
        <div id="test" className="container">
            <h2 style={{
                backgroundColor: "#BF1E2E",
                border: "none",
                color: "white",
                padding: "15px 32px",
                textAlign: "center",
                fontSize: "20px",
                margin: "0 auto",
            }}>Email Signature Generator</h2>
            <br />
            <form className="form" style={{ margin: "0 auto", width: "fit-content" }}>
                {/* Your input fields */}
                <CopyToClipboard text={generateSignature()} onCopy={() => { copyToClip("", imageUrl); toast.success("Signature copied to clipboard!"); }}>
                    <button
                        type="button"
                        style={{
                            backgroundColor: "#BF1E2E",
                            border: "none",
                            color: "white",
                            padding: "15px 32px",
                            textAlign: "center",
                            fontSize: "20px",
                            margin: "0 auto",
                            display: "block",
                            borderRadius: "5px",
                        }}
                    >
                        {copied ? 'Copied!' : 'Copy to Clipboard'}
                    </button>
                </CopyToClipboard>
            </form>
            <div dangerouslySetInnerHTML={{ __html: generateSignature() }}></div>
        </div>
    );
}

export default EmailSignature;
