import React, { useState, useRef } from 'react';
import { CopyToClipboard } from 'react-copy-to-clipboard';

const EmailSignature = () => {
  const [name, setName] = useState('');
  const [jobTitle, setJobTitle] = useState('');
  const [company, setCompany] = useState('');
  const [phone, setPhone] = useState('');
  const [email, setEmail] = useState('');
  const [website, setWebsite] = useState('');
  const [address, setAddress] = useState('');
  const [copied, setCopied] = useState(false); // State for copy status

  const handleInputChange = (event) => {
    const { name, value } = event.target;
    switch (name) {
      case 'name':
        setName(value);
        break;
      case 'jobTitle':
        setJobTitle(value);
        break;
      case 'company':
        setCompany(value);
        break;
      case 'phone':
        setPhone(value);
        break;
      case 'email':
        setEmail(value);
        break;
      case 'website':
        setWebsite(value);
        break;
      case 'address':
        setAddress(value);
        break;
      default:
        break;
    }
  };

  const generateSignature = () => {
    let signatureText = `${name}\n`;
    signatureText += `${jobTitle}\n`;
    if (company) {
      signatureText += `Company: ${company}\n`;
    }
    if (phone) {
      signatureText += `Phone: ${phone}\n`;
    }
    signatureText += `Email: ${email}`;
    return signatureText;
  };

  const handleCopy = () => {
    setCopied(true);
    setTimeout(() => setCopied(false), 2000); // Reset copied state after 2 seconds
  };

  return (
    <div className="container">
      <h2>Email Signature Generator</h2>
      <form className="form">
        {/* ... form fields ... */}
      </form>
      <div dangerouslySetInnerHTML={{ __html: generateSignature() }}></div>
      <CopyToClipboard text={generateSignatureText()} onCopy={handleCopy}>
        <button type="button">{copied ? 'Copied!' : 'Copy to Clipboard'}</button>
      </CopyToClipboard>
      {copied && <p>Signature copied to clipboard!</p>}
    </div>
  );
};

export default EmailSignature;
