import React, { useState } from 'react';
import { CopyToClipboard } from 'react-copy-to-clipboard';
import MailTemplate from './mail_template';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';



const EmailSignature = () => {
    const [name, setName] = useState('');
    const [jobTitle, setJobTitle] = useState('');
    const [phone, setPhone] = useState('');
    const [whatsapp, setWhatsapp] = useState('');
    const [email, setEmail] = useState('');
    const [company, setCompany] = useState(' Safiya Travels Pvt Ltd');
    const [line1, setLine1] = useState('39/4071B, Safiya Tower');
    const [line2, setLine2] = useState('Near Ravipuram Temple, Ravipuram Rd.');
    const [line3, setLine3] = useState('Kochi, Ernakulam,Kerala,India, 682016');

    const [copied, setCopied] = useState(false);
    const [image, setImage] = useState(null);
    const [imageUrl, setImageUrl] = useState(null);


    const handleInputChange = (event) => {
        const { name, value } = event.target;

        switch (name) {
            case 'name':
                setName(value);
                break;
            case 'jobTitle':
                setJobTitle(value);
                break;
            case 'phone':
                setPhone(value);
                break;
            case 'whatsapp':
                setWhatsapp(value);
                break;
            case 'email':
                setEmail(value);
                break;
            case 'company':
                setCompany(value);
                break;
            case 'line1':
                setLine1(value);
                break;
            case 'line2':
                setLine2(value);
                break;
            case 'line3':
                setLine3(value);
                break;
            case 'photo':
                const selectedFile = event.target.files[0];
                setImage(selectedFile);
                break;
            default:
                break;
        }
    };


    function copyToClip(htmlContent, imageDataURL) {

        let signatureText = `
        <div class="col-7">
            <div class="card" align:center;>
                <div class="card-body">
                    <div id="signature-container">
                        <table width="600" cellspacing="0" ; cellpadding="0" border="0">
                            <tbody>
                                <tr >
                                    <td style="padding-right:50px;padding-top:10px;" >
                                    <div style="width: 200px; height: 210px; overflow: hidden; border-radius: 15px;">
                                        <img src="${imageDataURL}" style="width: 100%;" />
                                    </div>
                                   
                                    </td>
                                    <td style="padding-bottom:100px; padding-top:130px;padding-right:20px; vertical-align:top;" valign="top">
                                        <table style="width: 300px;">
                                            <tbody>
                                                <tr>
                                                    <td style="text-align: center; font-weight:bold;line-height:50px; font-size:50px; color:#030303" id="empNameField">${name}</td>
                                                </tr>
                                                <td style="text-align: center;">
                                                    <hr style="width: 250px; height: 2px; background-color: #B5222B;">
                                                </td>
                                                <tr>
                                                    <td style="text-align: center;padding-bottom:30px;padding-top:5px;line-height:15px; color:#B5222B">
                                                        <span id="empDesignationField" style="font-size:25px;">${jobTitle}</span>
                                                    </td>
                                                </tr>
                                               
                                                <tr>
                                                    <td>
                                                        <span style="font-weight:600;">
                                                            <img class="signature-icon" style="width: 16px; height: 16px;" src="https://cdn.discordapp.com/attachments/1226811667250413608/1226812122537787452/whatsapp.png?ex=66296c93&is=66281b13&hm=9dcd8b4a087627a868767827a2098a37311e9caa87122f05b4fc1fcda49eb26a&">
                                                        </span>
                                                        &nbsp;
                                                        <span id="empWhatsappField" style="vertical-align: 4px;" class="v-align-offset">${whatsapp}</span>
                                                    </td>
                                                </tr>
                                                <tr>
                                                    <td style="line-height:0px;"    >
                                                        <span style="font-weight:600;">
                                                            <img class="signature-icon" style="color:#B5222B;width: 16px; height: 16px;" src="https://cdn.discordapp.com/attachments/1226811667250413608/1226812122290192384/smartphone_1.png?ex=66296c93&is=66281b13&hm=1e150c9389bec99e949ef62d6847ca94b65c7562ab114f8254304fb2813f851a&">
                                                        </span>
                                                        &nbsp;
                                                        <span id="empMobileField" style="vertical-align: 4px;" class="v-align-offset">${phone}</span>
                                                    </td>
                                                </tr>
                                                <tr>
                                                    <td>
                                                        <div style='padding-bottom:2px;font-size:14px;'>
                                                            <span style="font-weight:600;color:#e70312">
                                                                <img class="signature-icon" style="width: 16px; height: 16px;" src="https://cdn.discordapp.com/attachments/1226811667250413608/1226812121661177897/email.png?ex=66296c92&is=66281b12&hm=7ae2a4273fcd95ee07109ee8ec976460f8a08fa4f6d18dd14a3f4efb58032c5d&">
                                                            </span>
                                                            &nbsp;
                                                            <a id="empMailField" style="color:black;vertical-align: 4px;" class="v-align-offset" href="mailto:${email}" target="_blank">${email}</a>
                                                        </div>
                                                    </td>
                                                </tr>
                                            </tbody>
                                        </table>
                                    </td>
                                    <td style="text-align: center;padding-top:40px;">
                                        <hr style="width: 2px;border: 0; height: 165px; background-color: #B5222B;">
                                    </td>
                                    <td>
                                        <table style='width:500px;padding-top:35px;padding-left:50px;padding-right:0px;line-height:1.4;font-size:80%;color:rgb(0,0,1)' cellspacing="0" cellpadding="0" border="0">
                                            <tbody>
                                                <tr>
                                                    <td>
                                                        <div style='padding-bottom:2px;font-size:14px;line-height:35px;'>
                                                            <span style="font-weight:600;color:#e70312">  <img class="signature-icon" style="width: 16px; height: 16px;" src="
                                                            <div class="col-7">
                                                                <div class="card" align:center;>
                                                                    <div class="card-body">
                                                                        <div id="signature-container">
                                                                            <table width="600" cellspacing="0" ; cellpadding="0" border="0">
                                                                                <tbody>
                                                                                    <tr >
                                                                                        <td style="padding-right:50px;padding-top:10px;" >
                                                                                        <div style="width: 200px; height: 210px; overflow: hidden; border-radius: 15px;">
                                                                                            <img src="data:image/png;base64," style="width: 100%;" />
                                                                                        </div>
                                                                                       
                                                                                        </td>
                                                                                        <td style="padding-bottom:100px; padding-top:130px;padding-right:20px; vertical-align:top;" valign="top">
                                                                                            <table style="width: 300px;">
                                                                                                <tbody>
                                                                                                    <tr>
                                                                                                        <td style="text-align: center; font-weight:bold;line-height:50px; font-size:50px; color:#030303" id="empNameField">james</td>
                                                                                                    </tr>
                                                                                                    <td style="text-align: center;">
                                                                                                        <hr style="width: 250px; height: 2px; background-color: #B5222B;">
                                                                                                    </td>
                                                                                                    <tr>
                                                                                                        <td style="text-align: center;padding-bottom:30px;padding-top:5px;line-height:15px; color:#B5222B">
                                                                                                            <span id="empDesignationField" style="font-size:25px;">UI/UX</span>
                                                                                                        </td>
                                                                                                    </tr>
                                                                                                   
                                                                                                    <tr>
                                                                                                        <td>
                                                                                                            <span style="font-weight:600;">
                                                                                                                <img class="signature-icon" style="width: 16px; height: 16px;" src="https://cdn.discordapp.com/attachments/1226811667250413608/1226812122537787452/whatsapp.png?ex=66296c93&is=66281b13&hm=9dcd8b4a087627a868767827a2098a37311e9caa87122f05b4fc1fcda49eb26a&">
                                                                                                            </span>
                                                                                                            &nbsp;
                                                                                                            <span id="empWhatsappField" style="vertical-align: 4px;" class="v-align-offset">90898979890</span>
                                                                                                        </td>
                                                                                                    </tr>
                                                                                                    <tr>
