import React, { useState } from 'react';
import { CopyToClipboard } from 'react-copy-to-clipboard';

const EmailSignature = () => {
    const [name, setName] = useState('');
    const [jobTitle, setJobTitle] = useState('');
    const [company, setCompany] = useState('');
    const [phone, setPhone] = useState('');
    const [email, setEmail] = useState('');
    const [website, setWebsite] = useState('');
    const [address, setAddress] = useState('');
    const [copied, setCopied] = useState(false);


    const handleInputChange = (event) => {
        const { name, value } = event.target;
        switch (name) {
            case 'name':
                setName(value);
                break;
            case 'jobTitle':
                setJobTitle(value);
                break;
            case 'company':
                setCompany(value);
                break;
            case 'phone':
                setPhone(value);
                break;
            case 'email':
                setEmail(value);
                break;
            case 'website':
                setWebsite(value);
                break;
            case 'address':
                setAddress(value);
                break;
            default:
                break;
        }
    };
    const handleCopy = () => {
        setCopied(true);
        setTimeout(() => setCopied(false), 2000);
        console.log('Signature copied to clipboard!');
    };
    function copyToClip(str) {

        function listener(e) {
            e.clipboardData.setData("text/html", str);
            e.clipboardData.setData("text/plain", str);
            e.preventDefault();
        }
        document.addEventListener("copy", listener);
        document.execCommand("copy");
        document.removeEventListener("copy", listener);

    };

    const generateSignature = () => {
        let signatureText = `
        <div class="col-7">
            <div class="card">
                <div class="card-body">
                    <div id="signature-container">
                        <table width="600" cellspacing="0" cellpadding="0" border="0">
                            <tbody >
                                <tr>
                                    <td style="padding:15px 12px 0px 0px;vertical-align:top" width="50" valign="top">
                                        
                                        <span style='font-family:"HelveticaNeue-Light", "Helvetica Neue Light", "Helvetica Neue", Helvetica, Arial, "Lucida Grande", sans-serif;font-weight:600; font-size:20px;color:#030303' id="empNameField">${name}</span>
                                        <hr style="border: none; height: 1px; background-color: #e70312; margin: 8px 0;">                                        
                                        <div style="color:#e70312;padding-bottom:8px;font-family:'HelveticaNeue-Light', 'Helvetica Neue Light', 'Helvetica Neue', Helvetica, Arial, 'Lucida Grande', sans-serif;font-size:14px;font-weight: bold;">
                                            <span id="empDesignationField"> ${jobTitle}</span> 
                                           <p> <span style="font-weight:600;color:#e70312"> <img class="signature-icon" style="width: 16px; height: 16px;" src="https://www.dropbox.com/s/55914ht20w3ac5p/mobile.png?dl=1"></span>&nbsp;<span id="empMobileField" style="vertical-align: 4px;" class="v-align-offset">${phone}</span></p>                                          
                                               <div style='font-family:"HelveticaNeue-Light", "Helvetica Neue Light", "Helvetica Neue", Helvetica, Arial, "Lucida Grande", sans-serif;padding-bottom:2px;font-size:14px;'><span style="font-weight:600;color:#e70312"><img class="signature-icon" style="width: 16px; height: 16px;" src="https://www.dropbox.com/s/sbi2674lu40ucib/mail.png?dl=1"></span>&nbsp;<a id="empMailField" style="color:black;vertical-align: 4px;" class="v-align-offset" href="mailto:${email}" target="_blank">${email}</a></div>
                                        </div>
                                    </td>
                                    <td style="font-size:1em;padding:30px 0px 0px 12px;vertical-align:top;border-left:1px solid #e70312" valign="top">
                                        <table style='line-height:1.4;font-family:"HelveticaNeue-Light", "Helvetica Neue Light", "Helvetica Neue", Helvetica, Arial, "Lucida Grande", sans-serif;font-size:80%;color:rgb(0,0,1)' cellspacing="0" cellpadding="0" border="0">
                                            <tbody>
                                                <tr>
                                                    <td>
                                                        <div style='font-family:"HelveticaNeue-Light", "Helvetica Neue Light", "Helvetica Neue", Helvetica, Arial, "Lucida Grande", sans-serif;padding-bottom:2px;font-size:14px;'>
                                                            <span style="font-weight:600;color:#e70312"> </span>&nbsp;<span id="empMobileField" style="vertical-align: 4px;" class="v-align-offset">+91 8129778244</span>
                                                        </div>
                                                    </td>
                                                </tr>
                                                <tr>
                                                <td>
                                                    <div style='font-family:"HelveticaNeue-Light", "Helvetica Neue Light", "Helvetica Neue", Helvetica, Arial, "Lucida Grande", sans-serif;padding-bottom:2px;font-size:14px;'><span style="font-weight:600;color:#e70312"></span>&nbsp;<span style="vertical-align: 4px;" class="v-align-offset" id="compAddressField">www.safiya.travel</span></div>
                                                </td>
                                            </tr>
                                                <tr>
                                                    <td>
                                                        <div style='font-family:"HelveticaNeue-Light", "Helvetica Neue Light", "Helvetica Neue", Helvetica, Arial, "Lucida Grande", sans-serif;padding-bottom:2px;font-size:14px;'><span style="font-weight:600;color:#e70312"></span>&nbsp;<span style="vertical-align: 4px;" class="v-align-offset" id="compAddressField">${address}</span></div>
                                                    </td>
                                                </tr>
                                                <tr>
                                                    <td style="padding:3px 0px 0px">
                                                        <div style="font-family:'HelveticaNeue-Light', 'Helvetica Neue Light', 'Helvetica Neue', Helvetica, Arial, 'Lucida Grande', sans-serif;font-weight: bold;font-size:14px;">
                                                        </div>
                                                        <img id="compLogoField" alt="Safiya Logo" style="width:100px;border-radius:0%" src="https://www.dropbox.com/s/lenc3g9ywhrv1vu/safiya-signature-logo.png?dl=1" width="100" class="CToWUd" />

                                                    </td>
                                                </tr>
                                            </tbody>
                                        </table>
                                    </td>
                                </tr>
                            </tbody>
                        </table>
                        <div class="247container" style="display: none;">
                        <span style="font-weight:600;color:#e70312"> <img class="signature-icon" style="width: 40px; height: 40px;" src="https://www.dropbox.com/s/yin5joeb7ztlhpk/24x7-Phone-Number.png?dl=1"></span> <span style="vertical-align: 4px;font-size:20px;color:#e70312;font-weight: bold;" class="v-align-offset">24/7 Contact Number:</span>&nbsp;<span id="247contactField" style="vertical-align: 4px;font-weight:bold;font-size:20px;" class="v-align-offset">+91 8129778244</span>
    
                    </div>
    
                    <div id="bank-details-table-container">
                    </div>
                    <div id="signatureSuffixField">
                    </div>
    
                </div>
                <!-- <p class="text-center">Please Enter Your Data to Generate Signature</p> -->
            </div>
            <div class="card-footer">
                </div>
                </div>
        </div>`;

        return signatureText;
    };


    return (
        <div id="test" class="container">
        <div class="row">
          <div class="col-md-6">
            <h2>Email Signature Generator</h2>
            <form class="form">
              <h3>Personal Information</h3>
              <div class="form-group">
                <label for="name">Name:</label>
                <input type="text" id="name" name="name" value={name} onChange={handleInputChange} placeholder="Enter your name" />
              </div>
              <div class="form-group">
                <label for="jobTitle">Job Title:</label>
                <input type="text" id="jobTitle" name="jobTitle" value={jobTitle} onChange={handleInputChange} placeholder="Enter your job title" />
              </div>
              <div class="form-group">
                <label for="company">Company:</label>
                <input type="text" id="company" name="company" value={company} onChange={handleInputChange} placeholder="Enter your company name (optional)" />
              </div>
              </form>
          </div>
          <div class="col-md-6">
            <div class="signature-preview" style="border: 1px solid #ddd; padding: 10px;">
              <div dangerouslySetInnerHTML={{ __html: generateSignature() }}></div>
            </div>
            <CopyToClipboard text={generateSignature()} onCopy={copyToClip}>
              <button type="button">{copied ? 'Copied!' : 'Copy to Clipboard'}</button>
            </CopyToClipboard>
            {copied && <p>Signature copied to clipboard!</p>}
          </div>
        </div>
      </div>
      
    );
}

export default EmailSignature;
