import React, { useState } from 'react';
import { CopyToClipboard } from 'react-copy-to-clipboard';

const EmailSignature = () => {
    const [name, setName] = useState('');
    const [jobTitle, setJobTitle] = useState('');
    const [phone, setPhone] = useState('');
    const [whatsapp, setWhatsapp] = useState('');
    const [email, setEmail] = useState('');
    const [address, setAddress] = useState('Safiya Travels Pvt Ltd<br />39/4071B, Safiya Tower<br />Near Ravipuram Temple, Ravipuram Rd.<br />Kochi, Ernakulam, Kerala, India, 682016');
    const [copied, setCopied] = useState(false);
    const [image, setImage] = useState(null);

    const handleInputChange = (event) => {
        const { name, value } = event.target;

        switch (name) {
            case 'name':
                setName(value);
                break;
            case 'jobTitle':
                setJobTitle(value);
                break;
            case 'phone':
                setPhone(value);
                break;
            case 'whatsapp':
                setWhatsapp(value);
                break;
            case 'email':
                setEmail(value);
                break;
            case 'address':
                setAddress(value);
                break;
            case 'photo':
                const selectedFile = event.target.files[0];
                setImage(selectedFile);
                break;
            default:
                break;
        }
    };

    const handleCopy = () => {
        setCopied(true);
        setTimeout(() => setCopied(false), 2000);
        console.log('Signature copied to clipboard!');
    };

    function copyToClip(str) {
        function listener(e) {
            e.clipboardData.setData("text/html", str);
            e.clipboardData.setData("text/plain", str);
            e.preventDefault();
        }
        document.addEventListener("copy", listener);
        document.execCommand("copy");
        document.removeEventListener("copy", listener);
    };

    const generateSignature = () => {
        let signatureText = `
        <div class="col-7">
            <div class="card" align:center;>
                <div class="card-body">
                    <div id="signature-container">
                        <table width="600" cellspacing="0" ; cellpadding="0" border="0">
                            <tbody>
                                <tr >
                                <td style="padding-right:50px;" >
                                    <div style="width: 150px; height: 150px; overflow: hidden; border-radius: 15px;">
                                        <img src="${image ? URL.createObjectURL(image) : ''}" style="width: 100%;" />
                                    </div>
                                </td>

                                    <td style="padding-bottom:100px; padding-top:120px;padding-right:50px; vertical-align:top;" valign="top">
                                        <table style="width: 250px;">
                                            <tbody>
                                                <tr>
                                                    <td style="text-align: center; font-weight:bold;line-height:20px; font-size:30px; color:#030303" id="empNameField">${name}</td>
                                                </tr>
                                                <td style="text-align: center;">
                                                    <hr style="width: 200px; height: 2px; background-color: #B5222B;">
                                                </td>
                                                <tr>
                                                    <td style="text-align: center;padding-bottom:30px;line-height:15px; color:#B5222B">
                                                        <span id="empDesignationField" style="font-size:25px;">${jobTitle}</span>
                                                    </td>
                                                </tr>
                                               
                                                <tr>
                                                    <td>
                                                        <span style="font-weight:600;">
                                                            <img class="signature-icon" style="width: 16px; height: 16px;" src="https://cdn.discordapp.com/attachments/1226811667250413608/1226812122537787452/whatsapp.png?ex=662620d3&is=6613abd3&hm=aa1e17011a7b88b7d4824fa9e62f6e9cf017f5b58353ea71285b09e59643e9a8&">
                                                        </span>
                                                        &nbsp;
                                                        <span id="empWhatsappField" style="vertical-align: 4px;" class="v-align-offset">${whatsapp}</span>
                                                    </td>
                                                </tr>
                                                <tr>
                                                    <td>
                                                        <span style="font-weight:600;">
                                                            <img class="signature-icon" style="color:#B5222B;width: 16px; height: 16px;" src="https://cdn.discordapp.com/attachments/1226811667250413608/1226812122290192384/smartphone_1.png?ex=662620d3&is=6613abd3&hm=13a742978b5ee1a6b0dff1b8a0caf6e89ab9c6cbab152f778e932421eea6a770&">
                                                        </span>
                                                        &nbsp;
                                                        <span id="empMobileField" style="vertical-align: 4px;" class="v-align-offset">${phone}</span>
                                                    </td>
                                                </tr>
                                                <tr>
                                                    <td>
                                                        <div style='padding-bottom:2px;font-size:14px;'>
                                                            <span style="font-weight:600;color:#e70312">
                                                                <img class="signature-icon" style="width: 16px; height: 16px;" src="https://cdn.discordapp.com/attachments/1226811667250413608/1226812121661177897/email.png?ex=662620d2&is=6613abd2&hm=f2ab1391a2bebaf54e2ba19082f19029637abc17d9ef37353e1cadbb55e3cf73&">
                                                            </span>
                                                            &nbsp;
                                                            <a id="empMailField" style="color:black;vertical-align: 4px;" class="v-align-offset" href="mailto:${email}" target="_blank">${email}</a>
                                                        </div>
                                                    </td>
                                                </tr>
                                            </tbody>
                                        </table>
                                    </td>
                                    <td style="text-align: center;padding-top:40px;">
                                        <hr style="width: 2px;border: 0; height: 170px; background-color: #B5222B;">
                                    </td>
                                    <td>
                                        <table style='padding-top:35px;padding-left:50px;padding-right:350px;line-height:1.4;font-size:80%;color:rgb(0,0,1)' cellspacing="0" cellpadding="0" border="0">
                                            <tbody>
                                                <tr>
                                                    <td>
