import './App.css';
import { useState } from 'react';
import { sendPasswordResetEmail, signInWithEmailAndPassword, signOut } from 'firebase/auth';
import { auth } from './firebase/firebase';
import EmailSignature from './email_signature';


function App() {

  const logIn = () => {
    signInWithEmailAndPassword(auth, email, password).then((userCredential) => {
      const user = userCredential.user;
      setUser(user);
      console.log(user);
  }).catch((err) => {
    const error = err.message;
  });
  }
  const logout = () => {
    signOut(auth).then(() => setUser(null)).catch((err) => {
        const error = err.message;
    });
};
const handleSubmit = async (event) => {
  event.preventDefault();
  setErrorMessage("");
  setIsLoading(true);

  try {
    await signInWithEmailAndPassword(auth, email, password);
    console.log("User signed in successfully!");
  } catch (error) {
    console.error("Error signing in:", error.code, error.message);
    const friendlyMessage = getFriendlyErrorMessage(error.code);
    setErrorMessage(friendlyMessage);
  } finally {
    setIsLoading(false);
  }
};

  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const[user, setUser] = useState(null);
  const [errorMessage, setErrorMessage] = useState("");
  const [isLoading, setIsLoading] = useState(false);


  function getFriendlyErrorMessage(errorCode) {
    switch (errorCode) {
      case "auth/wrong-password":
        return "Incorrect password. Please try again.";
      case "auth/invalid-credential":
        return "Incorrect Credentials. Please try again.";
      case "auth/user-not-found":
        return "No user found with this email. Please check the email entered.";
      case "auth/invalid-email":
        return "Invalid email address. Please enter a valid email.";
      case "auth/user-disabled":
        return "This account has been disabled. Please contact support.";
      case "auth/too-many-requests":
        return "Too many attempts. Please try again later.";
      default:
        return "Failed to sign in. Please try again.";
    }
  }
  const handleForgotPassword = async () => {
    if (!email) {
      // Check if the email input is empty
      setErrorMessage(
        "Please enter your email address to reset your password."
      );
      return;
    }
    try {
      await sendPasswordResetEmail(auth, email);
      alert("Password reset email sent. Please check your inbox.");
    } catch (error) {
      console.error("Error sending password reset email:", error);
      setErrorMessage(getFriendlyErrorMessage(error.code));
    }
  };



  return (
    <div>
      {user ? <div> <EmailSignature /></div> :  <div className='App'><header className="App-header">
      </header>
      <body>
      <form onSubmit={handleSubmit} className="form-container">
      <img
        src="/favicon.svg"
        alt="Safiya Travels"
        className="form-logo"
        height={50}
      />
      <input
        type="email"
        className="form-input"
        value={email}
        onChange={(e) => setEmail(e.target.value)}
        placeholder="Email"
        required
      />
      <input
        type="password"
        className="form-input"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
        placeholder="Password"
        required
      />
      {errorMessage && <div className="error-message">{errorMessage}</div>}
      <button type="submit" className="form-button" onClick={logIn}>
        {isLoading ? "Signing In..." : "Sign In"}
      </button>
      <button
        type="button"
        className="forgot-password-button"
        onClick={handleForgotPassword}
      >
        Forgot Password?
      </button>
    </form>
      </body></div>}

    </div>
  );
}

export default App;
