import React, { useState } from 'react';
import { CopyToClipboard } from 'react-copy-to-clipboard';

const EmailSignature = () => {
    const [name, setName] = useState('');
    const [jobTitle, setJobTitle] = useState('');
    const [company, setCompany] = useState('');
    const [phone, setPhone] = useState('');
    const [email, setEmail] = useState('');
    const [website, setWebsite] = useState('');
    const [address, setAddress] = useState('');
    const [copied, setCopied] = useState(false);


    const handleInputChange = (event) => {
        const { name, value } = event.target;
        switch (name) {
            case 'name':
                setName(value);
                break;
            case 'jobTitle':
                setJobTitle(value);
                break;
            case 'company':
                setCompany(value);
                break;
            case 'phone':
                setPhone(value);
                break;
            case 'email':
                setEmail(value);
                break;
            case 'website':
                setWebsite(value);
                break;
            case 'address':
                setAddress(value);
                break;
            default:
                break;
        }
    };
    const handleCopy = () => {
        setCopied(true);
        setTimeout(() => setCopied(false), 2000);
        console.log('Signature copied to clipboard!');
    };
    function copyToClip(str) {

        function listener(e) {
            e.clipboardData.setData("text/html", str);
            e.clipboardData.setData("text/plain", str);
            e.preventDefault();
        }
        document.addEventListener("copy", listener);
        document.execCommand("copy");
        document.removeEventListener("copy", listener);

    };

    const generateSignature = (e) => {
        let signatureText = `
          <div class="col-7">
            <div class="card">
              <div class="card-body">
                <div id="signature-container">
                  <table width="600" cellspacing="0" cellpadding="0" border="0">
                    <tbody>
                      <tr>
                        <td style="padding:15px 12px 0px 0px;vertical-align:top" width="100" valign="top">
                          <img id="compLogoField" alt="Safiya Logo" style="width:140px;border-radius:0%" src="https://www.dropbox.com/s/lenc3g9ywhrv1vu/safiya-signature-logo.png?dl=1" width="100" class="CToWUd" />
                        </td>
                        <td style="font-size:1em;padding:0px 0px 0px 12px;vertical-align:top;border-left:1px solid #e70312" valign="top">
                          <table style='line-height:1.4;font-family:"HelveticaNeue-Light", "Helvetica Neue Light", "Helvetica Neue", Helvetica, Arial, "Lucida Grande", sans-serif;font-size:80%;color:rgb(0,0,1)' cellspacing="0" cellpadding="0" border="0">
                            <tbody>
                              <tr>
                                <td style="padding-bottom:0px"><span style='font-family:"HelveticaNeue-Light", "Helvetica Neue Light", "Helvetica Neue", Helvetica, Arial, "Lucida Grande", sans-serif;font-weight:600; font-size:20px;color:#e70312' id="empNameField">${name}</span></td>
                              </tr>
                              <tr> <td style="color:rgb(0,0,1);padding-bottom:8px">
                                  <div style='font-family:"HelveticaNeue-Light", "Helvetica Neue Light", "Helvetica Neue", Helvetica, Arial, "Lucida Grande", sans-serif;font-size:14px;font-weight: bold;'>&nbsp;<span id="empDesignationField">Executive Manager</span> | <span id="compNameField">Safiya Travels PVT LTD</div>
                                </td>
                              </tr>
                              </tbody>
                          </table>
                        </td>
                      </tr>
                      </tbody>
                  </table>
                </div>
              </div>
              <div class="card-footer">
              </div>
            </div>
          </div>`;
      
        return signatureText;
      };
      

    return (
        <div id="test" className="container">
            <h2>Email Signature Generator</h2>
            <form className="form">
                <label htmlFor="name">Name:</label>
                <input
                    type="text"
                    id="name"
                    name="name"
                    value={name}
                    onChange={handleInputChange}
                    placeholder="Enter your name"
                />
                <br />
                <label htmlFor="jobTitle">Job Title:</label>
                <input
                    type="text"
                    id="jobTitle"
                    name="jobTitle"
                    value={jobTitle}
                    onChange={handleInputChange}
                    placeholder="Enter your job title"
                />
                <br />
                <label htmlFor="company">Company:</label>
                <input
                    type="text"
                    id="company"
                    name="company"
                    value={company}
                    onChange={handleInputChange}
                    placeholder="Enter your company name (optional)"
                />
                <br />
                <label htmlFor="phone">Phone :</label>
                <input
                    type="text"
                    id="phone"
                    name="phone"
                    value={phone}
                    onChange={handleInputChange}
                    placeholder="Enter your phone number (optional)"
                />
                <br />
                <label htmlFor="email">Email :</label>
                <input
                    type="text"
                    id="email"
                    name="email"
                    value={email}
                    onChange={handleInputChange}
                    placeholder="Enter your email address"
                />
                <br />
                <label htmlFor="website">Website : </label>
                <input
                    type="text"
                    id="website"
                    name="website"
                    value={website}
                    onChange={handleInputChange}
                    placeholder="Enter your website URL (optional)"
                />
                <br />
                <label htmlFor="address">Address : </label>
                <input
                    type="text"
                    id="address"
                    name="address"
                    value={address}
                    onChange={handleInputChange}
                    placeholder="Enter your address (optional)"
                />
                <br />
            </form>
            <div dangerouslySetInnerHTML={{ __html: generateSignature() }}></div>
            <CopyToClipboard text={generateSignature()} onCopy={copyToClip}>
                <button type="button">{copied ? 'Copied!' : 'Copy to Clipboard'}</button>
            </CopyToClipboard>
            {copied && <p>Signature copied to clipboard!</p>}
        </div>
    );
}

export default EmailSignature;
