import React, { useState } from 'react';

const EmailSignatureGenerator = () => {
    const [name, setName] = useState('');
    const [jobTitle, setJobTitle] = useState('');
    const [company, setCompany] = useState('');
    const [phone, setPhone] = useState('');
    const [email, setEmail] = useState('');
    const [website, setWebsite] = useState('');
    const [address, setAddress] = useState('');

    const handleInputChange = (event) => {
        const { name, value } = event.target;
        switch (name) {
            case 'name':
                setName(value);
                break;
            case 'jobTitle':
                setJobTitle(value);
                break;
            case 'company':
                setCompany(value);
                break;
            case 'phone':
                setPhone(value);
                break;
            case 'email':
                setEmail(value);
                break;
            case 'website':
                setWebsite(value);
                break;
            case 'address':
                setAddress(value);
                break;
            default:
                break;
        }
    };

    const copyToClipboard = (text) => {
        navigator.clipboard.writeText(text)
            .then(() => alert('Signature copied to clipboard!'))
            .catch((error) => console.error('Unable to copy signature: ', error));
    };

    return (
        <div className="col-7">
            <div className="card">
                <div className="card-header">
                    <h5 className="text-center">Email Signature</h5>
                </div>
                <div className="card-body">
                    <div id="signature-container">
                        {/* Your signature HTML content here */}
                        <table>
                            <tbody>
                                <tr>
                                    <td>
                                        <span style={{ fontWeight: 'bold', float: 'left', fontSize: '14px', marginBottom: '2px' }}>Thanks and Regards,</span>
                                    </td>
                                </tr>
                            </tbody>
                        </table>
                        {/* Rest of the signature content */}
                    </div>
                </div>
                <div className="card-footer">
                    <div className="btn-group text-center float-right" role="group" aria-label="Basic example">
                        <button type="button" className="btn btn-warning reset"><i className="fa fa-times-circle mr-2"></i>Reset</button>
                        <button type="button" className="btn btn-danger clear-all"><i className="fa fa-times-circle mr-2"></i>Clear All</button>
                        <button type="button" className="btn btn-primary" onClick={() => copyToClipboard(document.getElementById('signature-container').innerHTML)}><i className="fa fa-copy mr-2"></i>Copy to Clipboard</button>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default EmailSignatureGenerator;
